        <td class="p-4 rounded-l-xl">{{translatedObject.name}}</td>
        <td class="p-4">{{translatedObject.startDateLabel}}</td>
        <td class="p-4">{{translatedObject.totalTimeLabel}}</td>
        <td class="p-4">{{translatedObject.objectStatus}}</td>
