<script>
import {TabGroup, TabList, Tab, TabPanels, TabPanel, TransitionRoot, TransitionChild, Dialog, DialogPanel,} from "@headlessui/vue";

export default {
  name:'ObjectHistory',
  props:['objectHistory','objectID'],
  components:{TabGroup, TabList, Tab, TabPanels, TabPanel, TransitionRoot, TransitionChild, Dialog, DialogPanel,},
  data(){
    return {
      tasks:[],
      selectedTab:0,
      isCompleteTaskModalOpen:false
    }
  },
  mounted() {
  },
  methods:{
     toggleCompleteTaskModal(s) {
          this.isCompleteTaskModalOpen = s;
          this.fetch(this.selectedTab)
     },
    fetch(selectedTab){
      this.tasks = []
      this.selectedTab = selectedTab
      let status = this.selectedTab===0?2:this.selectedTab===1?0:this.selectedTab===2?1:4
      // eslint-disable-next-line no-undef
      axios.get('list-tasks',{
        params:{
            status:status,
          object_id:this.objectID
        }
      })
      .then(response => {
        this.tasks = response.data.data
      })
    }
  },
